from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
left_motor_wheel = Motor(Port.A, Direction.CLOCKWISE)
right_motor_wheel = Motor(Port.E, Direction.COUNTERCLOCKWISE)
left_arm = Motor(Port.C, Direction.CLOCKWISE)
drive_base = DriveBase(left_motor_wheel, right_motor_wheel, 83, 130)


# The main program starts here.
# We are using -ve number to drive forward as our machine is in opposite direction.
drive_base.straight(-700)
# once driven straight turn right
drive_base.turn(-45)
# drive straight to touch the mission, at the sametime move the arm
drive_base.straight(-70)
left_arm.run_angle(-250, -135, Stop.BRAKE)
# turn right to complete the mission
drive_base.turn(-60)
# return back
left_arm.run_angle(-250, 135)
drive_base.straight(30)
drive_base.turn(-90)
drive_base.straight(-700)
